INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147515, 'Ramucirumab', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of ramucirumab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Ramucirumab can cause bleeding, including severe and sometimes fatal hemorrhagic complications.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously.', NULL, 'Concomitant use of ramucirumab and medications that interfere with platelet function or coagulation should be approached cautiously. Close clinical and laboratory observation for bleeding complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools. Ramucirumab should be permanently discontinued in patients who experience severe bleeding.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147678/', '[1] "Product Information. Cyramza (ramucirumab)." Eli Lilly and Company, Indianapolis, IN.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767363025);
