INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147587, 'Upadacitinib', 'Omacetaxine mepesuccinate', 'Major', 'Coadministration of upadacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and upadacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs or symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, or pain or burning during urination. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147750/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.', 1767363025);
