INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147615, 'Iohexol', 'Carteolol', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147778/', '[1] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[2] Costa N "Understanding contrast media." J Infus Nurs 27 (2004): 302-12[3] "Product Information. Visipaque (iodixanol)." Nycomed Inc, Princeton, NJ.[4] "Product Information. Ultravist (iopromide)." Berlex, Richmond, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147616, 'Iohexol', 'Chlorpromazine', 'Major', 'Intrathecal administration of iodinated contrast media may induce seizures. Although clinical data are generally lacking, there may be a theoretical risk of increased seizure potential when used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, theophylline). These agents are often individually epileptogenic and may have additive effects when combined.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient.', NULL, 'Drugs that can lower the seizure threshold should preferably be withheld for at least 48 hours prior to and 24 hours following intrathecal administration of iodinated contrast media, provided that temporary interruption of therapy does not pose an undue risk to the patient. Otherwise, close monitoring is advised during and after contrast administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/147779/', '[1] "Product Information. Amipaque (metrizamide)" Nycomed Inc, Princeton, NJ.[2] Hindmarsh T, Grepe A, Widen L "Metrizamide-phenothiazine interaction: report of a case with seizures following myelography." Acta Radiol Diagn (Stockh) 16 (1975): 129-35[3] "Product Information. Omnipaque 180 (iohexol)." Amersham Health, Princeton, NJ.[4] "Product Information. Osmovist 240 (iotrolan)." Berlex Canada Inc, Lachine, QC.[5] "Product Information. Isovue-M-200 (iopamidol)." Bracco Diagnostics Inc, Princeton, NJ.', 1767363025);
