INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147857, 'Rivastigmine', 'Ondansetron', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148020/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147858, 'Rizatriptan', 'Ondansetron', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', NULL, 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148021/', '[1] "Product Information. Akynzeo (netupitant-palonosetron)." Eisai Inc, Woodcliff Lake, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] "Product Information. Sancuso (granisetron)." ProStrakan Group, Bedminster, NJ.[6] "Product Information. Aloxi (palonosetron)." MGI Pharma Inc, Minnetonka, MN.[7] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (147859, 'Romidepsin', 'Ondansetron', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', NULL, 'Caution and clinical monitoring are recommended.', NULL, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148022/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Witchel HJ, Hancox JC, Nutt DJ "Psychotropic drugs, cardiac arrhythmia, and sudden death." J Clin Psychopharmacol 23 (2003): 58-77[3] Iannini PB "Cardiotoxicity of macrolides, ketolides and fluoroquinolones that prolong the QTc interval." Expert Opin Drug Saf 1 (2002): 121-8[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Ca