    ) VALUES (1412350, 'Ivabradine', 'Disopyramide', 'Major', 'Due to its bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased when ivabradine is used with drugs that prolong the QT interval.', NULL, 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided. Caution and cardiac monitoring are recommended if concomitant use is required.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
