        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1412354, 'Disopyramide', 'Ketoconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 such as ketoconazole may significantly increase the plasma concentrations of disopyramide, which is primarily metabolized by the isoenzyme. High plasma levels of disopyramide may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death. Ketoconazole itself can also prolong the QT interval, thus an additive pharmacodynamic effect with disopyramide may occur as well.', NULL, 'Coadministration of disopyramide with ketoconazole is considered contraindicated.', NULL, 'Coadministration of disopyramide with ketoconazole is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
