    ) VALUES (1412545, 'Disopyramide', 'Theophylline', 'Moderate', 'The concomitant use of disopyramide with cyclosporine or theophylline may theoretically result in increased serum concentrations of both drugs. The proposed mechanism is competitive inhibition of CYP450 3A4 metabolism.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of both drugs is recommended during concomitant therapy.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety of both drugs is recommended during concomitant therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
