    ) VALUES (1412589, 'Aminophylline', 'Disulfiram', 'Moderate', 'Disulfiram may inhibit the metabolism of theophyllines. Serum theophylline levels may be increased (by up to 50% ), as may adverse effects.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
