INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148254, 'Posaconazole', 'Osilodrostat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme. Clinically, high plasma levels of osilodrostat may increase the risk of adverse effects such as hypocortisolism (which may lead to life-threatening adrenal insufficiency), QT prolongation (which may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death), and elevated androgen and 11-deoxycorticosterone levels (the latter of which may activate mineralocorticoid receptors and cause hypokalemia, edema, and hypertension).', NULL, 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor.', NULL, 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor. Further dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range. Additionally, an electrocardiogram and serum electrolyte levels should also be obtained prior to initiating osilodrostat therapy and periodically thereafter.', 'Metabolism', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148417/', '[1] "Product Information. Isturisa (osilodrostat)." Recordati Rare Diseases Inc, Lebanon, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148255, 'Pralsetinib', 'Osilodrostat', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pralsetinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when pralsetinib is used with CYP450 3A inhibitors.', NULL, 'Caution is advised when pralsetinib is used with CYP450 3A inhibitors. Monitor closely for adverse effects whenever a CYP450 3A4 inhibitor is added to therapy. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'Futibatinib, Regorafenib, Copanlisib, Alpelisib, Trametinib, Erlotinib, Ponatinib, Neratinib, Erdafitinib, Nintedanib, Capmatinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148418/', '[1] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.[2] "Product Information. Gavreto (pralsetinib)." Blueprint Medicines Corporation, Cambridge, MA.', 1767369485);
