INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148349, 'Amprenavir', 'Osimertinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. However, no pharmacokinetic data are available from drug interaction studies. Because osimertinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, close monitoring for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases) is recommended, and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148512/', '[1] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148350, 'Osimertinib', 'Artesunate', 'Moderate', 'Coadministration with osimertinib may increase the exposure of breast cancer resistance protein (BCRP) transporter substrates. In vitro studies have shown that osimertinib is a competitive inhibitor of BCRP.', NULL, 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index.', NULL, 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index. Monitoring for signs and symptoms of increased exposure to the BCRP substrate should be considered whenever osimertinib is added to or withdrawn from therapy.', 'Others', 'Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148513/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148351, 'Osimertinib', 'Astemizole', 'Moderate', 'Coadministration with osimertinib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. In vitro, osimertinib has been shown to be a competitive inhibitor as well as inducer of CYP450 3A4.', NULL, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, 'Concomitant use of osimertinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Zanubrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148514/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148352, 'Bacillus calmette-guerin substrain tice live antigen', 'Osimertinib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Entrectinib, Pralsetinib, Dabrafenib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Larotrectinib, Vemurafenib, Lapatinib, Selumetinib, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/