INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148423, 'Idelalisib', 'Osimertinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. However, no pharmacokinetic data are available from drug interaction studies. Because osimertinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, close monitoring for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases) is recommended, and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, Regorafenib, More', 'Idelalisib, Tivozanib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148586/', '[1] "Product Information. Tagrisso (osimertinib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148424, 'Iloperidone', 'Osimertinib', 'Major', 'Iloperidone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged. Persistent QTc measurements exceeding 500 msec will require suspension of iloperidone therapy and immediate action to correct any concomitant risk factors before resuming treatment.', 'Synergism', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Brexpiprazole', 'Imatinib, Acalabrutinib, Pexidartinib, Tepotinib, Avapritinib, Selumetinib, Cobimetinib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148587/', '[1] "Product Information. Exforge HCT (amlodipine/hydrochlorothiazide/valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=p