INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148807, 'Salsalate', 'Oxaliplatin', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148970/', '[1] "Product Information. Eloxatin (oxaliplatin)." Sanofi Winthrop Pharmaceuticals, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148808, 'Samarium (153Sm) lexidronam', 'Oxaliplatin', 'Major', 'Theoretical concerns exist that chemotherapeutic agents and other bone marrow depressants may potentiate the myelosuppressive effects of samarium sm 153 lexidronam.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks.', NULL, 'The manufacturer recommends avoiding concomitant use of samarium sm 153 lexidronam with chemotherapy or external beam radiation therapy unless benefits are anticipated to outweigh the risks. Moreover, samarium sm 153 lexidronam should not be given after either of these treatments until there has been time for adequate marrow recovery. Caution and close monitoring of bone marrow function are advisable if coadministration with other myelotoxic agents is required. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, unusual bleeding or bruising, or signs of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148971/', '[1] "Product Information. Quadramet (samarium sm 153 lexidronam)" Berlex Laboratories, Richmond, CA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148809, 'Sargramostim', 'Oxaliplatin', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148972/', '[1] "Product Information. Leukine (sargramostim)." Immunex Corporation, Seattle, WA.[2] "Product Information. Granix (tbo-filgrastim)." Teva Pharmaceuticals USA, North Wales, PA.[3] "Product Information. Stemgen (ancestim)" Amgen, Thousand Oaks, CA.[4] "Product Information. Neupogen (filgrastim)." Amgen, Thousand Oaks, CA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148810, 'Pfizer-BioNTech Covid-19 Vaccine', 'Oxaliplatin', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148973/', '[1] "Product Information. Imovax Rabies (rabies vaccine)." sanofi pasteur, Swiftwater, PA.[2] Department of Health. National Health Service "Immunisation Against Infectious Disease - "The Green Book". Chapter 6: Contraindications and special considerations. Available from: URL: https://assets.publishing.service.gov.uk/government/uploads/system/uploads/attachment_data/file/655225/Greenbook_chap" ([2017 Aug]):[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] "Product Information. Havrix (hepatitis A vaccine)." SmithKline Beecham, Philadelphia, PA.[5] "Product Information. Fluzone Influenza Vaccine (influenza virus vaccine)." Connaught, Swiftwater, PA.[6] CDC Centers for Disease Control and Prevention "General Best Practice Guidelines for Immunization: Altered Immunocompetence. Available from: URL: https://www.cdc.gov/vaccines/hcp/acip-recs/general-recs/immunocompetence.pdf." ([2017, Apr 17]):[7] "Product Information. Biothrax (anthrax vaccine adsorbed)." Emergent BioSolutions Inc., Lansing, MI.[8] "Product Information. Omnihib (haemophilus b conjugate vaccine)." SmithKline Beecham, Philadelphia, PA.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Influenza Virus Vaccine, H5N1, Inactivated (influenza virus vaccine, H5N1, inactivated)." GlaxoSmithKline, Research Triangle Park, NC.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (148811, 'Selpercatinib', 'Oxaliplatin', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/148974/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365208);
