    ) VALUES (1412918, 'Valproic acid', 'Phenylbutyric acid', 'Major', 'Valproic acid can increase plasma ammonia levels and may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Hyperammonemic encephalopathy, sometimes fatal, has been reported following initiation of valproate therapy in patients with urea cycle disorders, particularly ornithine transcarbamylase deficiency.', NULL, 'Valproic acid should generally not be given to patients receiving phenylbutyrate therapy, as the use of valproic acid is considered contraindicated in patients with urea cycle disorders.', NULL, 'Valproic acid should generally not be given to patients receiving phenylbutyrate therapy, as the use of valproic acid is considered contraindicated in patients with urea cycle disorders.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
