    ) VALUES (1412936, 'Valproic acid', 'Tipranavir', 'Moderate', 'Coadministration with tipranavir/ritonavir may decrease the plasma concentrations of drugs that are substrates of UGT1A1. The mechanism is accelerated clearance due to induction of UGT1A1 activity by tipranavir.', NULL, 'Caution is advised if tipranavir/ritonavir must be used concomitantly with medications that undergo metabolism by UGT1A1, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if tipranavir/ritonavir must be used concomitantly with medications that undergo metabolism by UGT1A1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever tipranavir/ritonavir is added to or withdrawn from therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
