INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413240, 'Dofetilide', 'Bretylium', 'Major', 'Dofetilide should not be used with Class I or other Class III antiarrhythmic agents due to the potential for additive effects on myocardial refractoriness. Many of these agents, including dofetilide, can also cause prolongation of the QT interval, thus concomitant use may increase the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', 'DDInter', 1767317476, 'Class I (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, ibutilide, sotalol) antiarrhythmic agents should be withheld for at least 3 half-lives before administering dofetilide. In the case of amiodarone with its unpredictable pharmacokinetics, dofetilide should not be initiated until serum amiodarone levels are below 0.3 mcg/mL or amiodarone has been withdrawn for at least three months.', 'Synergism', 'Class I (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413241, 'Frangula purshiana bark', 'Dofetilide', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
