INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413295, 'Idelalisib', 'Dofetilide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dofetilide, which is partially metabolized by the isoenzyme. The use of dofetilide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
