INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149193, 'Pitolisant', 'Oxybutynin', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', NULL, 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149356/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149194, 'Polythiazide', 'Oxybutynin', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149357/', '[1] Osman MA, Welling PG "Influence of propantheline and metoclopramide on the bioavailability of chlorothiazide." Curr Ther Res Clin Exp 34 (1983): 404-8[2] Beermann B, Groschinsky-Grind M "Enhancement of the gastrointestinal absorption of hydrochlorothiazide by propantheline." Eur J Clin Pharmacol 13 (1978): 385-7', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149195, 'Potassium chloride', 'Oxybutynin', 'Major', 'Concomitant use of agents with anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, the class IA antiarrhythmic disopyramide) may potentiate the risk of upper gastrointestinal injury associated with oral solid formulations of potassium chloride. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents, thereby creating a high localized concentration of potassium ions in the region of a dissolving tablet or capsule and increasing the contact time with GI mucosa. Solid formulations of potassium chloride have been associated with upper GI bleeding and small bowel ulceration, stenosis, perforation, and obstruction.', NULL, 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects.', NULL, 'The use of certain oral solid formulations of potassium chloride is considered contraindicated in patients receiving agents with anticholinergic properties at sufficient doses to exert anticholinergic effects. A liquid formulation of potassium chloride should be considered. Patients prescribed a solid oral formulation should be advised to discontinue potassium therapy and contact their physician if they experience potential symptoms of upper GI injury such as severe vomiting, abdominal pain, distention, and gastrointestinal bleeding.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149358/', '[1] Lofgren RP, Rothe PR, Carlson GJ "Jejunal perforation associated with slow-release potassium chloride therapy." South Med J 75 (1982): 1154-5[2] Lambert JR, Newman A "Ulceration and stricture of the esophagus due to oral potassium chloride (slow release tablet) therapy." Am J Gastroenterol 73 (1980): 508-11[3] Farquharson-Roberts MA, Giddings AE, Nunn AJ "Perforation of small bowel due to slow release potassium chloride (slow-K)." Br Med J 3 (1975): 206[4] McMahon FG, Ryan JR, Akdamar K, Ertan A "Effect of potassium chloride supplements on upper gastrointestinal mucosa." Clin Pharmacol Ther 35 (1984): 852-5[5] Leijonmarck CE, Raf L "Gastrointestinal lesions and potassium chloride supplements." Lancet 1 (1985): 56-7[6] McMahon FG, Ryan JR, Akdamar K, Ertan A "Upper gastrointestinal lesions after potassium chloride supplements: a controlled clinical trial." Lancet 2 (1982): 1059-61[7] Heffernan SJ, Murphy JJ "Ulceration of small intestine and slow-release potassium tablets." Br Med J 2 (1975): 746[8] "Product Information. K-Dur (potassium chloride)." Schering Laboratories, Kenilworth, NJ.[9] Leijonmarck CE, Raf L "Ulceration of the small intestine due to slow-release potassium chloride tablets." Acta Chir Scand 151 (1985): 273-8[10] Weiss SM, Rutenberg HL, Paskin DL, Zaren HA "Gut lesions due to slow-release KCI tablets." N Engl J Med 296 (1977): 111-2[11] Wynn V "Potassium chloride and bowel ulceration." Br Med J 5477 (1965): 1546[12] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[13] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[14] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[15] "Product Information. Rexulti (brexpiprazole)." Otsuka American Phar