INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149267, 'Sibutramine', 'Oxymetazoline (ophthalmic)', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when sibutramine is combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating therapy with sibutramine and monitored at regular intervals thereafter. Dose reduction or discontinuation of sibutramine should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149430/', '[1] "Product Information. Meridia (sibutramine)." Knoll Pharmaceutical Company, Whippany, NJ.[2] Mendelson J, Jones RT, Upton R, Jacob P 3rd "Methamphetamine and ethanol interactions in humans." Clin Pharmacol Ther 57 (1995): 559-68[3] "Product Information. Didrex (benzphetamine)" Pharmacia and Upjohn, Kalamazoo, MI.[4] "Product Information. Suprenza (phentermine)." Akrimax Pharmaceuticals, Cranford, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149268, 'Solriamfetol', 'Oxymetazoline (ophthalmic)', 'Moderate', 'Additive or synergistic effects on blood pressure and/or heart rate may occur when solriamfetol is combined with other drugs that increase blood pressure and/or heart rate. The use of solriamfetol alone has been associated with dose-dependent increases in blood pressure and heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate.', NULL, 'Caution is advised if solriamfetol is used with other drugs that increase blood pressure and/or heart rate. Blood pressure should be monitored prior to initiating solriamfetol therapy and regularly during treatment. In addition, hypertension should be controlled prior to treatment, and any new-onset hypertension or exacerbations of pre-existing hypertension that occur during solriamfetol therapy should be treated. Discontinuation of solriamfetol should be considered in patients who experience an increase in blood pressure or heart rate that cannot be managed with dose reduction of solriamfetol or other appropriate medical intervention.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149431/', '[1] "Product Information. Sunosi (solriamfetol)." Jazz Pharmaceuticals, Palo Alto, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149269, 'Tapentadol', 'Oxymetazoline (ophthalmic)', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149432/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Sav