INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149747, 'Ozanimod', 'Lomefloxacin', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149910/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149748, 'Ozanimod', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149911/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149749, 'Ozanimod', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'INTERVAL: The administration of inactivated, killed, or otherwise noninfectious vaccines during ozanimod therapy is generally safe but may be associated with a diminished or suboptimal immunologic response. Vaccination may be less effective during and for up to three months after discontinuation of ozanimod therapy.', NULL, 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy.', NULL, 'Immune response to vaccines may be reduced during and for up to three months after discontinuation of ozanimod therapy. In patients who have recently been vaccinated, ozanimod therapy should preferably be postponed for one month to allow the full effect of vaccination to occur.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149912/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (149750, 'Ozanimod', 'Mazindol', 'Moderate', 'Coadministration of sympathomimetic agents with drugs that possess monoamine oxidase inhibition (MAOI) activity may precipitate severe hypertensive reactions and hyperpyrexia. Death has occurred in some reported cases. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons secondary to MAOI activity. MAOIs also slow the metabolism of some sympathomimetics such as amphetamines, which may potentiate their effect on the release of norepinephrine and other monoamines from adrenergic nerve endings. Although the interaction has primarily involved nonselective MAOIs, hypertensive crisis has been reported with selective MAO-B inhibitors. Because an active metabolite of ozanimod inhibits MAO-B in vitro, the interaction may theoretically occur with ozanimod. In a placebo-controlled crossover study involving healthy subjects, coadministration of ozanimod with pseudoephedrine did not potentiate the effects on blood pressure; however, ozanimod did increase the pseudoephedrine-induced heart rate response by approximately 3 beats per minute (bpm).', NULL, 'Until more information is available, concomitant use of ozanimod with sympathomimetic agents should be avoided when possible.', NULL, 'Until more information is available, concomitant use of ozanimod with sympathomimetic agents should be avoided when possible. Blood pressure and other vitals should be monitored if coadministration is required.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/149913/', '[1] Smookler S, Bermudez AJ "Hypertens