INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413711, 'Dolasetron', 'Toremifene', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413712, 'Dolasetron', 'Torasemide', 'Major', 'The risk of arrhythmia may be increased in patients taking dolasetron and diuretics. Dolasetron induced ECG changes (PR and QTc prolongation; QRS widening) have been observed in healthy volunteers and in controlled clinical trials. Diuretics may further predispose the patient to arrhythmia by inducing electrolyte abnormalities.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', 'Synergism', 'Close clinical and laboratory monitoring for cardiac rhythm and electrolyte disturbances is recommended if this combination is used.', NULL, NULL, 'Major', 'pharmacodynamic');
