INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413960, 'Donepezil', 'Piroxicam', 'Minor', 'Donepezil may increase gastric acid secretion by increasing cholinergic activity. The risk for gastrointestinal bleeding is, therefore, theoretically increased in patients receiving donepezil in combination with a nonsteroidal anti-inflammatory agent. Caution is recommended when this combination is used.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413961, 'Donepezil', 'Pitolisant', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 1767317476, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413962, 'Donepezil', 'Posaconazole', 'Minor', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of donepezil, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
