INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1413992, 'Donepezil', 'Rivastigmine', 'Moderate', 'A synergistic effect may be expected when acetylcholinesterase inhibitors are administered concurrently with other cholinesterase inhibitors or cholinergic agonists.', 'DDInter', 1767317476, 'Monitoring for excessive cholinergic effects and seizure activity is recommended. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, increased salivation, cold sweats, slow or irregular heartbeat, muscle weakness or cramps, dizziness, headache, seizures, or respiratory distress.', 'Synergism', 'Monitoring for excessive cholinergic effects and seizure activity is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
