INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150049, 'Selpercatinib', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when selpercatinib is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development of serious adverse effects such as QT interval prolongation, liver transaminase and bilirubin elevations, hypertension, hemorrhage, edema and hypersensitivity reactions (e.g., fever, rash, arthralgias/myalgias with concurrent decreased platelets or transaminitis), and the dosing of selpercatinib adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150212/', '[1] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Retevmo (selpercatinib)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150050, 'Selumetinib', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3. Increased exposures to selumetinib and N-desmethyl selumetinib may increase the risk and/or severity of serious adverse effects such as cardiomyopathy (decrease in left ventricular ejection fraction by 10% or more below baseline), ocular toxicity (blurred vision, photophobia, cataracts, ocular hypertension, retinal pigment epithelial detachment, retinal vein occlusion), gastrointestinal toxicity (diarrhea, colitis), skin toxicity (dermatitis acneiform, maculopapular rash, eczema), and musculoskeletal toxicity (creatine phosphokinase elevations, myalgia, rhabdomyolysis).', NULL, 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when selumetinib is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be required based on clinical response and tolerance. Please refer to the product labeling for specific guidelines on dosing adjustments.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150213/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150051, 'Sildenafil', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of sildenafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of sildenafil should be considered.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if sildenafil is coadministered with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for sildenafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150214/', '[1] "Product Information. Revatio (sildenafil)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[3] Warrington JS, Shader RI, vonMoltke LL, Greenblatt DJ "In vitro biotransformation of sildenafil (Viagra): Identification of human cytochromes and potential drug interactions." Drug Metab Disposition 28 (2000): 392-7[4] Hyland R, Roe GH, Jones BC, Smith DA "Identification of the cytochrome P450 enzymes involved in the N-demethylation of sildenafil." Br J Clin Pharmaacol 51 (2001): 239-48[5] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150052, 'Silodosin', 'Palbociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of silodosin, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if silodosin is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to silodosin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the silodosin dosage adjusted as necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150215/', '[1] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.', 1767365208);
