INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150258, 'Drotrecogin alfa', 'Panobinostat', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150421/', '[1] "Product Information. Xigris (drotrecogin alfa)." Lilly, Eli and Company, Indianapolis, IN.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150259, 'Echinacea', 'Panobinostat', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150422/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365208);
