INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150527, 'Pazopanib', 'Pantoprazole', 'Major', 'Coadministration with proton pump inhibitors or H2-receptor antagonists may significantly decrease the oral bioavailability of pazopanib and reduce its concentrations in plasma. The solubility of pazopanib is pH-dependent, thus an increase in pH may interfere with its absorption.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided.', NULL, 'Concomitant use of pazopanib with proton pump inhibitors and H2-receptor antagonists should generally be avoided. If acid-suppression therapy is required, short-acting antacids should be considered, with dosing separated by several hours from pazopanib dosing. Similarly, when coadministration with an H2-receptor antagonist is required, it has been recommended that pazopanib be taken without food at least 2 hours before or 10 hours after a dose of the H2-receptor antagonist.', 'Absorption', 'Tinidazole, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Vonoprazan, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, Ibrutinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150690/', '[1] van Leeuwen RW, van Gelder T, Mathijssen RH, Jansman FG "Drug-drug interactions with tyrosine-kinase inhibitors: a clinical perspective." Lancet Oncol 15 (2014): e315-e326[2] Yu G, Zheng QS, Wang DX, Zhou HH, Li GF "Drug interactions between tyrosine-kinase inhibitors and acid suppressive agents: more than meets the eye." Lancet Oncol 15 (2014): e469-70[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[5] Tan AR, Gibbon DG, Stein MN, et al. "Effects of ketoconazole and esomeprazole on the pharmacokinetics of pazopanib in patients with solid tumors." Cancer Chemother Pharmacol 71 (2013): 1635-43[6] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[7] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (150528, 'Pexidartinib', 'Pantoprazole', 'Major', 'Coadministration with proton pump inhibitors (PPIs) may significantly decrease the bioavailability and therapeutic effects of pexidartinib. The proposed mechanism is a pH-dependent reduction in drug dissolution and/or absorption. The effect of coadministration with an H2-receptor antagonist or locally-acting antacids on pexidartinib pharmacokinetics is unknown.', NULL, 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided.', NULL, 'Concomitant use of pexidartinib with proton pump inhibitors should be avoided. If acid-suppression therapy is required, pexidartinib may be administered 2 hours before or 2 hours after taking a locally-acting antacid, or if using an H2-receptor antagonist, administer pexidartinib at least 2 hours before or 10 hours after taking the H2-receptor antagonist.', 'Absorption', 'Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Amoxicillin', 'Entrectinib, Dabrafenib, Asciminib, Pacritinib, Tepotinib, Avapritinib, Vemurafenib, Selumetinib, Ribociclib, Cobimetinib, Ibrutinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/150691/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Nexium (esomeprazole)" Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767369485