INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414086, 'Esmolol', 'Dopamine', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'DDInter', 1767317476, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414087, 'Ethotoin', 'Dopamine', 'Moderate', 'Coadministration of dopamine and intravenous hydantoin derivatives may result in sudden and profound hypotension and bradycardia. The mechanism may be a combination of catecholamine depletion and hydantoin-induced myocardial depression, and the effect may be dose-rate dependent. Fatalities have been reported, but causality is unknown. Data are available for intravenous phenytoin only.', 'DDInter', 1767317476, 'Extreme caution and frequent blood pressure monitoring are recommended if concomitant use is necessary. Alternative anticonvulsants may be considered.', 'Synergism', 'Extreme caution and frequent blood pressure monitoring are recommended if concomitant use is necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
