INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414114, 'Iobenguane (I-123)', 'Dopamine', 'Moderate', 'Coadministration with drugs that block norepinephrine uptake or deplete norepinephrine stores may decrease iobenguane I-123 uptake in neuroendocrine tumors and lead to false-negative imaging results.', 'DDInter', 1767317476, 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', 'Others', 'When medically feasible, any drug that blocks norepinephrine uptake or deplete norepinephrine stores should be discontinued for at least five biological half-lives before iobenguane I-123 administration.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414115, 'Iobenguane (I-131)', 'Dopamine', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', 'DDInter', 1767317476, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414116, 'Isocarboxazid', 'Dopamine', 'Major', 'Indirect- or mixed-acting sympathomimetic amines may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). Death has occurred in some reported cases. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and increased liberation of catecholamines (indirect sympathomimetic activity).', 'DDInter', 1767317476, 'In general, indirect- and mixed-acting sympathomimetic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with sympathomimetic agents.', 'Synergism', 'In general, indirect- and mixed-acting sympathomimetic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414117, 'Isoetharine', 'Dopamine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 1767317476, 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
