INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414145, 'Dopamine', 'Methylergometrine', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317476, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414146, 'Dopamine', 'Methysergide', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 1767317476, 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414147, 'Dopamine', 'Metipranolol (ophthalmic)', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', 'DDInter', 1767317476, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414148, 'Dopamine', 'Metoclopramide', 'Minor', 'Metoclopramide may attenuate the pressor and antikaliuretic effects of dopamine. The mechanism is not known. Management consists of discontinuing metoclopramide if necessary.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
