INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414364, 'Ivabradine', 'Timolol (ophthalmic)', 'Moderate', 'Coadministration of ivabradine with other drugs that can slow the heart rate such as beta-blockers, calcium channel blockers, and digitalis may increase the risk of excessive bradycardia or other conduction disturbances. Non-cardiac drugs that may also cause bradycardia include atazanavir, fingolimod, lacosamide, lithium, mefloquine, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', 'DDInter', 1767317476, 'Caution is advised when ivabradine is prescribed with other bradycardic drugs. Heart rate should be closely monitored, and the dosage of ivabradine adjusted accordingly. Patients should be advised to report significant decreases in heart rate or symptoms such as dizziness, lightheadedness, fatigue, and hypotension.', 'Synergism', 'Caution is advised when ivabradine is prescribed with other bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414365, 'Timolol (ophthalmic)', 'Lanreotide', 'Moderate', 'Somatostatin analogs (e.g., octreotide, lanreotide) can decrease heart rate and may potentiate the bradycardic effects of drugs like beta-blockers, calcium channel blockers, digitalis glycosides, and certain antiarrhythmic agents (e.g., class I antiarrhythmics, adenosine, amiodarone).', 'DDInter', 1767317476, 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers. Heart rate and blood pressure should be monitored.', 'Synergism', 'Caution is advised if somatostatin analogs are prescribed with agents that exhibit bradycardic effects, including ophthalmic formulations of beta-blockers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
