INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414461, 'Indapamide', 'Doxazosin', 'Moderate', 'The hypotensive effects of thiazide diuretics and alpha-adrenergic blockers may be additive. Postural hypotension may occur.', 'DDInter', 1767317476, 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
