INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1414590, 'Doxazosin', 'Troleandomycin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and effects of doxazosin. In vitro studies suggest that the primary pathway for elimination of doxazosin is via CYP450 3A4.', 'DDInter', 1767317476, 'Caution is advised if doxazosin is used concomitantly with potent CYP450 3A4 inhibitors. Blood pressure and pharmacologic response to doxazosin should be monitored closely whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the doxazosin dosage adjusted if necessary. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, orthostasis, syncope, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medication affects them.', 'Metabolism', 'Caution is advised if doxazosin is used concomitantly with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
