INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151097, 'Pazopanib', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lomitapide, which is primarily metabolized by the isoenzyme.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton.', NULL, 'The maximum recommended dosage of lomitapide is 30 mg daily when used in combination with weak CYP450 3A4 inhibitors such as amiodarone, atorvastatin, bicalutamide, cyclosporine, danazol, isoniazid, ivacaftor, lapatinib, nilotinib, pazopanib, zafirlukast, and zileuton. Caution is advised because these agents may also have additive hepatotoxic effects with lomitapide. Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin monitored prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151260/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Self TH, Chrisman CR, Baciewicz AM, Bronze MS "Isoniazid drug and food interactions." Am J Med Sci 317 (1999): 304-11[5] Uragoda CG, Kottegoda SR "Adverse reactions to isoniazid on ingestion of fish with a high histamine content." Tubercle 58 (1977): 83-9[6] "Product Information. INH (isoniazid)." Ciba Pharmaceuticals, Summit, NJ.[7] Smith CK, Durack DT "Isoniazid and reaction to cheese." Ann Intern Med 88 (1978): 520-1[8] Dimartini A "Isoniazid, tricyclics and the ''''cheese reaction''''." Int Clin Psychopharmacol 10 (1995): 197-8', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151098, 'Pazopanib', 'Lonafarnib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of pazopanib during and for 2 weeks after treatment with itraconazole. If coadministration is unavoidable, a reduction of the pazopanib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the pazopanib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Further dosage reductions may be needed if adverse effects occur during therapy. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the pazopanib dosage is adjusted upward.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151261/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151099, 'Pazopanib', 'Lyme disease vaccine (recombinant OspA)', 'Moderate', 'The administration of Lyme disease vaccine to immunosuppressed patients may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy.', NULL, 'The manufacturer suggests considering deferral of vaccination against Lyme disease until 3 months after discontinuation of immunosuppressive therapy. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151262/', '[1] "Product Information. Lymerix (Lyme disease vaccine)." SmithKline Beecham, Philadelphia, PA.', 1767365208);
