INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151153, 'Rilonacept', 'Pazopanib', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151316/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151154, 'Ripretinib', 'Pazopanib', 'Moderate', 'Coadministration of CYP450 3A4 inhibitors with ripretinib may increase the plasma concentrations and the risk of adverse effects (e.g., palmar-plantar erythrodysesthesia, hypertension, cardiac dysfunction, arthralgia/myalgia) of ripretinib and its active metabolite (DP-5439). The proposed mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', NULL, 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor.', NULL, 'Caution is recommended when ripretinib is used concomitantly with a CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151317/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Qinlock (ripretinib)." Deciphera Pharmaceuticals, Waltham, MA.[3] Gunston GD, Mehta U "Potentially serious drug interactions with grapefruit juice." S Afr Med J 90 (2000): 41[4] Bailey DG, Arnold JMO, Spence JD "Grapefruit juice and drugs - how significant is the interaction." Clin Pharmacokinet 26 (1994): 91-8[5] Fuhr U, Maier-Bruggemann A, Blume H, et al. "Grapefruit juice increases oral nimodipine bioavailability." Int J Clin Pharmacol Ther 36 (1998): 126-32[6] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[7] Libersa CC,