INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151162, 'Rosuvastatin', 'Pazopanib', 'Moderate', 'Concomitant use of pazopanib with simvastatin may increase the risk of ALT elevations, possibly due to additive hepatotoxic effects.', NULL, 'Caution is advised if pazopanib is used in combination with simvastatin.', NULL, 'Caution is advised if pazopanib is used in combination with simvastatin. Serum liver transaminases and bilirubin should be measured prior to initiation of pazopanib and regularly during treatment as recommended in the product labeling. If ALT elevations occur, simvastatin should be discontinued and guidelines for pazopanib dosage adjustments should be followed, or alternatives to pazopanib considered. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, light colored stools, and jaundice. Insufficient data are available to assess the risk of pazopanib administered concomitantly with other HMG-CoA reductase inhibitors.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151325/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[3] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[4] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[5] Lilja JJ, Kiv