INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151234, 'Typhoid vaccine (live)', 'Pazopanib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151397/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151235, 'Ubrogepant', 'Pazopanib', 'Moderate', 'Coadministration with moderate or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors. If needed, a second ubrogepant dose should be avoided within 24 hours of the initial dose when used with moderate CYP450 3A4 inhibitors.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151398/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151236, 'Upadacitinib', 'Pazopanib', 'Major', 'Coadministration of upadacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if upadacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and upadacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs or symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, or pain or burning during urination. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151399/', '[1] "Product Information. Rinvoq (upadacitinib)." AbbVie US LLC, North Chicago, IL.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151237, 'Ustekinumab', 'Pazopanib', 'Moderate', 'The use of ustekinumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa.', NULL, 'Caution is advised if ustekinumab must be used in patients who have recently received or are receiving treatment with other immunosuppressive or myelosuppressive drugs, and vice versa. Close clinical and laboratory monitoring for the development of severe hematologic adverse effects is recommended both during and after discontinuation of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151400/', '[1] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151238, 'Valbenazine', 'Pazopanib', 'Moderate', 'Valbenazine may cause modest prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Central nervous system (CNS)-depressant effects may be additively or synergistically increased in patients taking valbenazine with certain other drugs that cause these effects, especially in elderly or debilitated patients.', NULL, 'Caution and clinical monitoring are recommended if concomitant use of valbenazine with other drugs that can prolong the QT interval is required.', NULL, 'Caution and clinical monitoring are recommended if concomitant use of valbenazine with other drugs that can prolong the QT interval is required. Valbenazine is not recommended for use in patients with congenital long QT syndrome or with arrhythmias associated with a prolonged QT interval. In patients with other risk factors for QT prolongation, the QT interval should be assessed before increasing the dose of valbenazine. The manufacturer recommends that valbenazine dosage be reduced to 40 mg once daily in patients on concomitant therapy with a strong CYP450 3A4 inhibitor (e.g., itraconazole, ketoconazole, clarithromycin). Valbenazine dose reduction should also be considered in patients on concurrent therapy with a strong CYP450 2D6 inhibitor (e.g., paroxetine, fluoxetine, quinidine), or in patients who are poor metabolizers of CYP450 2D6.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151401/', '[1] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[2] "Product Information. Ingrezza (valbenazine)." Neurocrine Biosciences, Inc., San Diego, CA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151239, 'Valganciclovir', 'Pazopanib', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151402/', '[1] "Product Information. Valcyte (valganciclovir)" Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[5] "Product Information. Cytovene (ganciclovir)." Genentech, South San Francisco, CA.[6] Jung D, Griffy K, Dorr A "Effect of food on high-dose oral ganciclovir disposition in HIV-positive subjects." J Clin Pharmacol 39 (1999): 161-5[7] Lavelle J, Follansbee S, Trapnell CB, Buhles WC, Griffy KG, Jung D, Dorr A, Conner J "Effect of food on the relative bioavailability of oral ganciclovir." J Clin Pharmacol 36 (1996): 238-41', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151240, 'Vandetanib', 'Pazopanib', 'Major', 'Vandetanib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of vandetanib with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of vandetanib with other drugs that can prolong the QT interval should generally be avoided. Should treatment with other QT-prolonging drugs be required, the manufacturer recommends more frequent monitoring of the ECG as well as serum potassium, magnesium, and calcium levels.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151403/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Vandetanib (vandetanib)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[4] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151241, 'Varicella Zoster Vaccine (Recombinant)', 'Pazopanib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151404/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
