INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151246, 'Voriconazole', 'Pazopanib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of pazopanib during and for 2 weeks after treatment with itraconazole. If coadministration is unavoidable, a reduction of the pazopanib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the pazopanib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Further dosage reductions may be needed if adverse effects occur during therapy. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the pazopanib dosage is adjusted upward.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151409/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151247, 'Voxelotor', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4.', NULL, 'Caution is advised if pazopanib is prescribed in combination with inhibitors of CYP450 3A4. Pharmacologic response to pazopanib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the pazopanib dosage adjusted as necessary. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151410/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151248, 'Yellow Fever Vaccine', 'Pazopanib', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151411/', '[1] Fauci AS, Braunwald E, Isselbacher KJ, Wilson JD, Martin JB, Kasper DL, Hauser SL, Longo DL, eds. "Harrison''s Principles of Internal Medicine. 14th ed." New York, NY: McGraw-Hill Health Professionals Division (1998):[2] "Product Information. YF-Vax (yellow fever vaccine)." sanofi pasteur, Swiftwater, PA.[3] "Product Information. Attenuvax (measles virus vaccine, live, attenuated)." Merck & Co, Inc, West Point, PA.[4] Kriner P, Lopez K, Leung J, Harpaz R, Bialek SR "Notes from the field: varicella-associated death of a vaccinated child with leukemia - California, 2012." MMWR Morb Mortal Wkly Rep 63 (2014): 161[5] "Product Information. M-M-R II (measles/mumps/rubella virus vaccine)." Merck & Company Inc, West Point, PA.[6] Charkoudian LD, Kaiser GM, Steinmetz RL, Srivastava SK "Acute retinal necrosis after herpes zoster vaccination." Arch Ophthalmol 129 (2011): 1495-7[7] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[8] "Product Information. Meruvax II (rubella virus vaccine, live)." Merck & Co, Inc, West Point, PA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
