INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151288, 'Filgrastim', 'Pegaspargase', 'Moderate', 'INTERVAL: The safety and efficacy of hematopoietic growth factors such as colony-stimulating factors (G-CSF and GM-CSF) and stem cell factors (SCF) given simultaneously with cancer chemotherapy have not been established. Theoretical concerns exist regarding their concomitant administration because hematopoietic growth factors stimulate myeloid cell proliferation while antineoplastic agents primarily target rapidly dividing cells.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', NULL, 'Because of the potential sensitivity of rapidly dividing myeloid cells to cancer chemotherapy, hematopoietic growth factors should not be used within 24 hours before or 24 hours after administration of antineoplastic agents.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151451/', '[1] "Product Information. Leukine (sargramostim)." Immunex Corporation, Seattle, WA.[2] "Product Information. Granix (tbo-filgrastim)." Teva Pharmaceuticals USA, North Wales, PA.[3] "Product Information. Stemgen (ancestim)" Amgen, Thousand Oaks, CA.[4] "Product Information. Neupogen (filgrastim)." Amgen, Thousand Oaks, CA.', 1767365208);
