INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151499, 'Aminophylline', 'Peginterferon alfa-2a', 'Moderate', 'The coadministration with interferons has been associated with decreases in clearance and increases in plasma concentration and elimination half-life of theophylline. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes, particularly, CYP450 1A2.', NULL, 'Caution is advised if theophylline or other methylxanthines are administered with an interferon.', NULL, 'Caution is advised if theophylline or other methylxanthines are administered with an interferon. Pharmacologic response and serum theophylline levels should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their methylxanthine regimen. Patients should be advised to contact their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151662/', '[1] "Product Information. Intron A (interferon alfa-2b)." Schering Laboratories, Kenilworth, NJ.[2] Sakai H, Okamoto T, Kikkawa Y "Suppression of hepatic drug metabolism by the interferon inducer polyriboinosinic acid:polyribocitidylic acid." J Pharmacol Exp Ther 263 (1992): 381-6[3] Jonkman JH, Nicholson KG, Farrow PR, et al "Effects of alpha-interferon on theophylline pharmacokinetics and metabolism." Br J Clin Pharmacol 27 (1989): 795-802[4] Kramer P, Tsuru M, Cook CE, et al "Effect of influenza vaccine on warf