INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151562, 'Telbivudine', 'Peginterferon alfa-2a', 'Major', 'The risk and severity of peripheral neuropathy may be increased during coadministration of telbivudine with alfa interferons. The mechanism of interaction has not been described, although these agents individually have been associated with neuropathy and may have additive effects during concomitant use.', NULL, 'The safety and efficacy of telbivudine in combination with alfa interferons for the treatment of chronic hepatitis B have not been demonstrated, thus concomitant use should generally be avoided.', NULL, 'The safety and efficacy of telbivudine in combination with alfa interferons for the treatment of chronic hepatitis B have not been demonstrated, thus concomitant use should generally be avoided. Coadministration of telbivudine with pegylated interferon alfa-2a is specifically contraindicated by the manufacturer. Patients treated with these agents should be advised to report any numbness, tingling, and/or burning sensations in the arms and/or legs, with or without gait disturbance. Telbivudine therapy should be interrupted if peripheral neuropathy is suspected, and discontinued if confirmed.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151725/', '[1] "Product Information. PEG-Intron (peginterferon alfa-2b)" Schering Corporation, Kenilworth, NJ.[2] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.[3] "Product Information. Pegasys (peginterferon alfa-2a)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Intron A (interferon alfa-2b)." Schering Laboratories, Kenilworth, NJ