INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151570, 'Tofacitinib', 'Peginterferon alfa-2a', 'Major', 'Coadministration of tofacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids. Lymphoma and other malignancies have also been observed with tofacitinib use, with or without concomitant immunosuppressants.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and tofacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151733/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151571, 'Topotecan', 'Peginterferon alfa-2a', 'Moderate', 'The use of topotecan with other cytotoxic agents is likely to result in greater myelosuppression due to additive or synergistic pharmacologic effects. Severe neutropenia and neutropenic sepsis resulting in death were reported with topotecan and cisplatin.', NULL, 'Caution is advised if topotecan is used with other cytotoxic agents.', NULL, 'Caution is advised if topotecan is used with other cytotoxic agents. Dosage reductions may be required, and bone marrow function should be closely monitored. Patients should be advised to contact their physician if they develop signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151734/', '[1] "Product Information. Hycamtin (topotecan)." SmithKline Beecham, Philadelphia, PA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151572, 'Trabectedin', 'Peginterferon alfa-2a', 'Moderate', 'Coadministration of trabectedin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when trabectedin is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Monitoring of alkaline phosphatase, bilirubin, AST, and ALT should occur regularly during trabectedin treatment in accordance with the product labeling, or as often as necessary when clinical symptoms develop.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151735/', '[1] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Yondelis (trabectedin)." Janssen Pharmaceuticals, Titusville, NJ.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365208);
