INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151790, 'Candida albicans', 'Pemetrexed', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151953/', '[1] "Product Information. Spherulin (coccidioidin skin test)." ALK Laboratories Inc, Wallingford, CT.[2] "Product Information. Tuberculin PPD (Mantoux) (tuberculin purified protein derivative)." Connaught Laboratories Ltd, Toronto, ON.[3] "Product Information. Candin (candida albicans extract)." Nielsen Biosciences Inc, San Diego, CA.[4] "Product Information. MSTA Mumps Skin Test Antigen (mumps skin test antigen)." Aventis Pharmaceuticals, Swiftwater, PA.[5] "Product Information. Multitest CMI (skin test antigens, multiple)." Aventis Pharmaceuticals, Swiftwater, PA.[6] "Product Information. Histolyn-Cyl (histoplasmin)." ALK Laboratories Inc, Wallingford, CT.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151791, 'Carbenicillin', 'Pemetrexed', 'Moderate', 'Coadministration with drugs that are eliminated by active tubular secretion may delay and/or decrease the clearance of pemetrexed. The mechanism is competitive inhibition of the renal excretion of pemetrexed, which is primarily eliminated unchanged via glomerular filtration and active tubular secretion. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, 'Patients receiving pemetrexed in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary. The potential for increased toxicity of pemetrexed such as bone marrow suppression should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/151954/', '[1] "Product Information. Alimta (pemetrexed)." Lilly, Eli and Company, Indianapolis, IN.', 1767365208);
