INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151879, 'Rilonacept', 'Pemetrexed', 'Moderate', 'The use of interleukin-1 blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Interleukin-1 blockade alone may cause neutropenia and serious infections, and the risk may be increased in the presence of other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established.', NULL, 'The safety and efficacy of interleukin-1 blockers in immunosuppressed patients have not been established. Until more information is available, interleukin-1 blockers should preferably not be administered to immunosuppressed patients, or they should be prescribed with extreme caution.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152042/', '[1] "Product Information. Kineret (anakinra)." Amgen, Thousand Oaks, CA.[2] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[3] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (151880, 'Rofecoxib', 'Pemetrexed', 'Moderate', 'INTERVAL: Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the plasma concentrations of pemetrexed. The mechanism has not been described but may be related to NSAID inhibition of renal prostaglandins. Use of NSAIDs has been associated with nephropathy manifested as elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure. Since pemetrexed is primarily eliminated unchanged by renal excretion, coadministration with NSAIDs may result in delayed and/or decreased clearance.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function.', NULL, 'Ibuprofen at 400 mg and aspirin at 325 mg four times a day, or less, may be used with pemetrexed in patients with normal renal function. However, caution is advised in patients with mild to moderate renal insufficiency (creatinine clearance 45 to 79 mL/min). These patients should avoid taking NSAIDs with short elimination half-lives (e.g., diclofenac, etodolac, fenoprofen, flurbiprofen, ibuprofen, indomethacin, ketoprofen, ketorolac, meclofenamate, mefenamic acid, sulindac, tolmetin, low dosages of salicylates) 2 days before to 2 days after pemetrexed administration. If concomitant administration is necessary, patients should be monitored closely for toxicity, especially myelosuppression, nephrotoxicity, and gastrointestinal toxicity.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/1