        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1414693, 'Fospropofol', 'Doxepin', 'Moderate', 'The concomitant use of general anesthetics with tricyclic antidepressants may increase the risk of hypotension and cardiac arrhythmias. The presence of pancuronium may increase the risk of tachyarrhythmias. Enflurane may also increase the risk of seizures when given to patients who have been taking tricyclic antidepressants.', NULL, 'Close monitoring is recommended when these drugs are used together.', NULL, 'Close monitoring is recommended when these drugs are used together.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
