INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (152358, 'Aminophylline', 'Pentoxifylline', 'Moderate', 'Coadministration with pentoxifylline may increase the serum concentrations of theophylline. The mechanism of interaction has not been described, but may involve competitive metabolism as both drugs are methylxanthine derivatives.', NULL, 'Caution is advised if theophylline preparations are used in combination with pentoxifylline.', NULL, 'Caution is advised if theophylline preparations are used in combination with pentoxifylline. Serum theophylline concentrations should be monitored more closely following the initiation, discontinuation or change of dosage of pentoxifylline, and the theophylline dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152521/', '[1] Cohen IA, Johnson CE, Wesolowicz L, Converse-Swastek K "Effect of pentoxifylline and its metabolites on three theophylline assays." Clin Pharm 7 (1988): 457-61[2] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Cummings DM, Rocci ML Jr, Green P, Saccar C "Interference potential of pentoxifylline and its major metabolite with theophylline assays." Am J Hosp Pharm 42 (1985): 2717-8[4] Ellison MJ, Horner RD, Willis SE, Cummings DM "Influence of pentoxifylline on steady-state theophylline serum concentrations from sustained-release formulations." Pharmacotherapy 10 (1990): 383-6', 1767363025);
