INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (152471, 'Vemurafenib', 'Pentoxifylline', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of pentoxifylline and its active hydroxy metabolite (M1), both of which are substrates of the isoenzyme.', NULL, 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary.', NULL, 'Pharmacologic response to pentoxifylline should be monitored more closely whenever a potent or moderate CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the pentoxifylline dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience serious adverse effects such as bleeding, angina, or arrhythmias.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152634/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Magnusson M, Bergstrand IC, Bjorkman S, Heijl A, Roth B, Hoglund P "A placebo-controlled study of retinal blood flow changes by pentoxifylline and metabolites in humans." Br J Clin Pharmacol 61 (2006): 138-47', 1767363025);
