        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1415162, 'Doxepin (topical)', 'Levothyroxine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', NULL, 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', NULL, 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
