        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1415353, 'Doxepin (topical)', 'Thioridazine', 'Major', 'Coadministration of thioridazine with another phenothiazine or a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of both drugs. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered. In addition, these agents individually can cause prolongation of the QT interval, thus concomitant administration may result in elevated risk of ventricular arrhythmias including ventricular tachycardia and torsade de pointes.', NULL, 'The use of thioridazine with drugs that can inhibit CYP450 2D6 and/or prolong the QT interval is considered contraindicated.', NULL, 'The use of thioridazine with drugs that can inhibit CYP450 2D6 and/or prolong the QT interval is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
