    ) VALUES (1415445, 'Iron sucrose', 'Doxercalciferol', 'Moderate', 'Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of vitamin D analogs. The proposed mechanism involves nonspecific adsorption to the ferric ion component of sucroferric oxyhydroxide, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Oral formulations of vitamin D analogs should not be prescribed with sucroferric oxyhydroxide.', NULL, 'Oral formulations of vitamin D analogs should not be prescribed with sucroferric oxyhydroxide.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
