INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153043, 'Retapamulin (topical)', 'Pexidartinib', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', NULL, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', NULL, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range. The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4. Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Dabrafenib, Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153206/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Sonata (zaleplon)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (153044, 'Ribociclib', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Tivozanib, Futibatinib, Trametinib, Asciminib, Pexidartinib', 'Futibatinib, Regorafenib, Trametinib, Erdafitinib, Nintedanib, Ribociclib, Dacomitinib, Tivozanib, Alectinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153207/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
