INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415494, 'Fingolimod', 'Doxorubicin', 'Major', 'Coadministration of fingolimod with antineoplastic, immunosuppressive, or other immune-modulating therapies may increase the risk of infections. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues. Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 1767317476, 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated. Caution is advised during coadministration. A complete blood count is recommended prior to starting fingolimod if a recent one (i.e., within last 6 months) is not available. Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'The safety and efficacy of fingolimod in combination with antineoplastic, immunosuppressive, or immune-modulating agents have not been evaluated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415495, 'Flecainide', 'Doxorubicin', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415496, 'Flibanserin', 'Doxorubicin', 'Moderate', 'Coadministration with flibanserin may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when flibanserin is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever flibanserin is added to or withdrawn from therapy.', 'Others', 'Caution is advised when flibanserin is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415497, 'Floxuridine', 'Doxorubicin', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals. Dosing adjustments may be necessary. The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1415498, 'Fluconazole', 'Doxorubicin', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', 'DDInter', 1767317476, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
